    T([844,894,944,1092,1142,1192,1242,1292,1342,1392,1491,1541,1591,1641,1691,1741,1791,1841,1891,1945,1995,2045,2095,2145,2195,2248,2298,2348,2400]) = 1;

    [V,~] = eig(T);
    S = V(46:48,:)./repmat(V(49,:),3,1);

    I = ~isnan(S(1,:)) & ~isinf(S(1,:));
    %I = I & ~imag(S(1,:)); % uncomment this line for real roots only
    S = S(:,I);

end