    A = U\[M1(:,1:end-4)*P M1(:,end-3:end)];
    B = R(:,1:33)\[R(:,34:end) Q'*M2(:,end-3:end)];
    M = [-A(:,end-32:end)+A(:,1:end-33)*B; -B];

    P1 = [67:91 (1:62)*P 63:66];
    P2 = [67,11,12,13,68,17,18,69,22,70,71,32,33,34,35,36,72,37,38,39,40,73,41,42,43,74,44,45,75,46,76,77,47,48,49,50,78,51,52,53,79,54,55,80,56,81,82,57,58,59,83,60,61,84,62,85,86,63,64,87,65,88,89,66,90,91];
    T = getT(M,P1(1:end-33),P1(end-32:end),P2);

    [V,~] = eig(T);
    S = V(30:32,:)./repmat(V(33,:),3,1);

    I = ~isnan(S(1,:)) & ~isinf(S(1,:));
    %I = I & ~imag(S(1,:)); % uncomment this line for real roots only
    S = S(:,I);

end